<?php
require "config.php";

// Security: Direct access prohibited without prior upload
if (!isset($_SESSION['current_image_id'])) {
    header("Location: upload.php");
    exit;
}

$imgId = $_SESSION['current_image_id'];

// Recovery of the 3 variants generated by Java
// We sort by ID to be sure that the order is always Bicubic -> Bilinear -> Nearest
$stmt = $db->prepare("SELECT * FROM mosaic_proposals WHERE image_id = ? AND strategy LIKE 'PREVIEW_%' ORDER BY id ASC");
$stmt->execute([$imgId]);
$proposals = $stmt->fetchAll(PDO::FETCH_ASSOC);

// 2. Processing the 'ORDER' Click
if (isset($_POST['choose_render']) && isset($_POST['proposal_id']) && isset($_POST['algo_target'])) {

    $selectedPropId = intval($_POST['proposal_id']);
    $algoId = intval($_POST['algo_target']); // The ID of the algorithm C (3, 4 or 5)

    // Building the strategy for Java: "ALGO_3", "ALGO_4", etc.
    $newStrategy = "ALGO_" . $algoId;

    // Update BDD: We change the mode and reset the counter to wake up Java
    $upd = $db->prepare("UPDATE mosaic_proposals SET strategy = ?, total_bricks_count = 0 WHERE id = ?");
    $upd->execute([$newStrategy, $selectedPropId]);

    // Save for the cart
    $_SESSION['final_proposal_id'] = $selectedPropId;

    header("Location: cart.php"); // Redirection to the basket
    exit;
}
?>

<?php include "header.php"; ?>

<div class="container" style="max-width: 1200px; margin: 0 auto; padding: 40px 20px;">

    <div style="text-align:center; margin-bottom:40px;">
        <h2 style="color: var(--text); margin-bottom:10px;"><?= msg('results_title') ?></h2>
        <p style="color: #64748b;">Chaque rendu utilise un algorithme de construction différent.</p>
    </div>

    <div style="display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 30px;">

        <?php if (empty($proposals)): ?>
            <div style="grid-column: 1/-1; text-align: center; padding: 60px; background:white; border-radius:12px; box-shadow:0 4px 6px rgba(0,0,0,0.05);">
                <div class="spinner" style="margin:0 auto 20px; border-top-color:var(--accent);"></div>
